import { Component, Output, EventEmitter, Input, OnChanges, inject } from '@angular/core';
import { FormArray, FormControl, FormGroup, Validators } from '@angular/forms';

import { Book } from '../../shared/book';
import { AsyncValidatorsService } from '../shared/async-validators.service';
import { atLeastOneValue, isbnFormat } from '../shared/validators';

@Component({
  selector: 'bm-book-form',
  templateUrl: './book-form.component.html',
  styleUrls: ['./book-form.component.css']
})
export class BookFormComponent implements OnChanges {
  @Input() book?: Book;
  @Output() submitBook = new EventEmitter<Book>();

  form = new FormGroup({
    title: new FormControl('', {
      nonNullable: true,
      validators: Validators.required,
    }),
    subtitle: new FormControl('', { nonNullable: true }),
    isbn: new FormControl('', {
      nonNullable: true,
      validators: [Validators.required,isbnFormat],
      asyncValidators: inject(AsyncValidatorsService).isbnExists()
    }),
    description: new FormControl('', { nonNullable: true }),
    published: new FormControl('', { nonNullable: true }),
    authors: this.buildAuthorsArray(['']),
    thumbnailUrl: new FormControl('', { nonNullable: true })
  });

  get fa() {
    return this.form.controls.authors;
  }

  ngOnChanges(): void {
    if (this.book) {
      this.setValues(this.book);
      this.setMode(true);
    } else {
      this.setMode(false);
    }
  }

  private setValues(book: Book) {
    this.form.patchValue(book);
    this.form.setControl('authors', this.buildAuthorsArray(book.authors));
  }

  private setMode(isEditing: boolean) {
    const isbnControl = this.form.controls.isbn;
    isEditing ? isbnControl.disable() : isbnControl.enable();
  }

  private buildAuthorsArray(authors: string[]) {
    return new FormArray(
      authors.map(v => new FormControl(v, { nonNullable: true })),
      atLeastOneValue
    );
  }

  addAuthorControl() {
    this.fa.push(new FormControl('', { nonNullable: true }));
  }

  submitForm() {
    const formValue = this.form.getRawValue();
    const authors = formValue.authors.filter(author => !!author);
    const newBook: Book = { ...formValue, authors };
    this.submitBook.emit(newBook);
  }
}
<form [formGroup]="form" (ngSubmit)="submitForm()">
  <label for="title">Title</label>
  <input id="title" formControlName="title">
  <bm-form-errors
    controlName="title"
    [messages]="{ required: 'Title is required' }">
  </bm-form-errors>

  <label for="subtitle">Subtitle</label>
  <input id="subtitle" formControlName="subtitle">

  <label for="isbn">ISBN</label>
  <input id="isbn" formControlName="isbn">
  <bm-form-errors
    controlName="isbn"
    [messages]="{
      required: 'ISBN is required',
      isbnformat: 'ISBN must have 10 or 13 chars',
      isbnexists: 'ISBN already exists'
    }">
  </bm-form-errors>

  <label>Authors</label>
  <button type="button" class="add"
    (click)="addAuthorControl()">
    + Author
  </button>
  <fieldset formArrayName="authors">
    <input
      *ngFor="let a of fa.controls; index as i"
      [attr.aria-label]="'Author ' + i"
      [formControlName]="i">
  </fieldset>
  <bm-form-errors
    controlName="authors"
    [messages]="{ atleastonevalue: 'At least one author required' }">
  </bm-form-errors>

  <label for="description">Description</label>
  <textarea id="description" formControlName="description"></textarea>

  <label for="published">Published</label>
  <input type="date" useValueAsLocalIso id="published" formControlName="published">

  <label for="thumbnailUrl">Thumbnail URL</label>
  <input type="url" id="thumbnailUrl" formControlName="thumbnailUrl">

  <button type="submit" [disabled]="form.invalid">
    Save
  </button>
</form>
